# Source global configuration and data loading explicitly
source("global.R")

library(shiny)
library(shinydashboard)
library(DT)
library(leaflet)
library(plotly)
library(heatmaply)

ui <- dashboardPage(
  dashboardHeader(title = "GIScience Journals"),

  dashboardSidebar(
    sidebarMenu(
      menuItem("Overview", tabName = "overview", icon = icon("dashboard")),
      menuItem("Rankings", tabName = "rankings", icon = icon("list-ol")),
      menuItem("Trends", tabName = "trends", icon = icon("chart-line")),
      menuItem("Geography", tabName = "geography", icon = icon("globe")),
      menuItem("Collaboration", tabName = "collab", icon = icon("project-diagram")),
      menuItem("Journal Insights", tabName = "insights", icon = icon("lightbulb")),
      menuItem("About", tabName = "about", icon = icon("info-circle"))
    )
  ),

  dashboardBody(
    tags$head(tags$style(HTML('
        /* Gator Lab Theme */
        .skin-blue .main-header .logo {
          background-color: #FA4616;
          color: #fff;
          font-weight: bold;
        }
        .skin-blue .main-header .logo:hover {
          background-color: #d1380f;
        }
        .skin-blue .main-header .navbar {
          background-color: #0021A5;
        }
        .skin-blue .main-sidebar {
          background-color: #1a1a1a;
        }
        .skin-blue .sidebar-menu > li.active > a, 
        .skin-blue .sidebar-menu > li:hover > a {
          border-left-color: #FA4616; 
          background: #333;
        }
        /* Custom Box Status Colors */
        .box.box-solid.box-primary>.box-header {
          background-color: #0021A5;
          border-color: #0021A5;
        }
        .box.box-solid.box-warning>.box-header {
          background-color: #FA4616;
          border-color: #FA4616;
          color: #fff;
        }
        .bg-blue { background-color: #0021A5 !important; }
        .bg-yellow { background-color: #ff9600 !important; } /* Softer orange for accents */
        .bg-green { background-color: #22884c !important; }
    '))),
    tabItems(
      # --- Overview Tab ---
      # --- Overview Tab ---
      tabItem(tabName = "overview",
        fluidRow(
          box(width = 12, title = "GIScience Publication Trends Dashboard", status = "primary", solidHeader = TRUE,
              HTML(
                "<p>This dashboard accompanies the paper <a href='https://doi.org/10.1080/13658816.2024.2347306'><b>Assessing publication trends in selected GIScience journals</b></a> published in the <i>International Journal of Geographical Information Science</i> in 2024 with subsequent data updates. The goal is to help the GIScience community find
                suitable venues for publications, as well as to assess our field in terms of important concepts such as open access penetration and international collaboration.</p>
                 <p>For more details on methodology, limitations, and journal metrics, please see the <b>About</b> tab.</p>"
              )
          )
        ),
        fluidRow(
          valueBoxOutput("totalJournalsBox", width = 4),
          valueBoxOutput("totalArticlesBox", width = 4),
          valueBoxOutput("oaShareBox", width = 4),
        #  valueBoxOutput("avgHBox", width = 3)
        ),
        fluidRow(
          box(width = 12, title = "Output Growth (Articles per Year)", status = "primary",
              plotlyOutput("overviewPlot", height = "350px"),
              helpText("Total number of articles published in the tracked journals.")

          )#,
          #box(width = 4, title = "Top Journals (by H-Index)", status = "warning",
          #    div(style = "overflow-x: scroll;", tableOutput("overviewTable"))
          #)
        ),
        fluidRow(
          box(width = 12, title = "Journal Metadata", status = "primary", solidHeader = TRUE,
              div(style = "overflow-x: scroll;", DTOutput("journalMetaTable")),
              helpText("Publishers: T&F = Taylor & Francis"),
              helpText("Peer-review: D=double-blind; S=single-blind;"),
              helpText("OA: Open Access"),
              helpText("The preprint column indicates whether depositing a preprint at article submission is allowed.")
          )
        ),

        fluidRow(
          box(width = 12, title = "Key Insights", status = "success", solidHeader = TRUE,
              uiOutput("dynamicInsights")
          )
        )
      ),

      # --- Rankings Tab ---
      # --- Rankings Tab ---
      # --- Rankings Tab ---
      tabItem(tabName = "rankings",
        fluidRow(
          box(width = 12, title = "Journal Meta Ranking", status = "warning", solidHeader = TRUE,
            sidebarLayout(
              sidebarPanel(
                width = 3,
                selectInput("metaRankYear", "Select Year:", choices = sort(unique(metrics_ranked$year), decreasing = TRUE)),
                checkboxGroupInput("metaRankMetrics", "Select Metrics for Meta Ranking:",
                                   choices = c("Impact Factor" = "Rank_IF", 
                                               "CiteScore" = "Rank_CiteScore", 
                                               "SJR" = "Rank_SJR", 
                                               "SNIP" = "Rank_SNIP",
                                               "5-Year IF" = "Rank_IF5y",
                                               "Immediacy Index" = "Rank_Imm",
                                               "Article Influence" = "Rank_Influ",
                                               "Eigenfactor" = "Rank_Eigen"),
                                   selected = c("Rank_IF", "Rank_CiteScore", "Rank_SJR", "Rank_SNIP"))
              ),
              mainPanel(
                width = 9,
                plotlyOutput("metaRankPlot", height = "600px")
              )
            ),
            helpText("The 'Meta Ranking' is calculated as the arithmetic mean of the ranks from the selected metrics. Points represent the mean rank; (error) bars show the range (min to max rank) across selected metrics.")
          )
        ),
        fluidRow(
          box(width = 12, title = "Journal Rankings Table", status = "primary", solidHeader = TRUE,
              div(style = "margin-bottom: 15px; overflow-x: scroll;",
                selectInput("rankYear", "Table Year:", choices = 2018:2025, selected = 2025, width = "200px")
              ),
              div(style="overflow-x: scroll;", DTOutput("rankTable"))
          )
        ),
        fluidRow(
          box(width = 12, title = "Rank Evolution", status = "primary", solidHeader = TRUE,
              div(style = "margin-bottom: 15px; overflow-x: scroll;",
                  selectInput("rankMetric", "Metric Focus:", 
                              choices = c("Impact Factor" = "IF", "SJR" = "SJR", "CiteScore" = "CiteScore", "SNIP" = "SNIP",
                                          "5-Year Impact Factor" = "IF5y", "Immediacy Index" = "Imm", 
                                          "Article Influence" = "Influ", "Eigenfactor" = "Eigen"),
                              width = "200px")
              ),
              plotlyOutput("rankPlot", height = "500px"),
              helpText("Cells are left empty if score is missing for a metric in a particular year.")
          )
        ),
        fluidRow(
          box(width = 12, title = "Movers & Shakers", status = "success", solidHeader = TRUE,
              uiOutput("rankInsights")
          )
        )
      ),

      # --- Trends Tab ---
      tabItem(tabName = "trends",
        fluidRow(
          box(width = 12, title = "Trend Settings", status = "warning", solidHeader = TRUE,
              column(6,
                  selectInput("trendJournals", "Select Journals to Compare:", choices = NULL, multiple = TRUE, width = "100%")
              ),
              column(6,
                  selectInput("trendMetric", "Select Metric for History:",
                              choices = c("Impact Factor" = "IF", "SJR" = "SJR", "CiteScore" = "CiteScore", "SNIP" = "SNIP",
                                          "5-Year Impact Factor" = "IF5y", "Immediacy Index" = "Imm", 
                                          "Article Influence" = "Influ", "Eigenfactor" = "Eigen"),
                              selected = "IF", width = "100%")
              )
          ),
        ),
        fluidRow(
          box(width = 12, title = uiOutput("trendBoxTitle"),
              plotlyOutput("metricTrendPlot"),
              helpText("Raw metric scores over time for selected journals.")
          )
        ),
        fluidRow(
          box(width = 6, title = "Article Volume (Selected Journals)",
              plotlyOutput("volumePlot"),
              helpText("Total number of articles published in selected journals.")
          ),
          box(width = 6, title = "Open Access Adoption (Selected Journals)",
              plotlyOutput("trendPlotOA"),
              helpText("Total number of OA articles in selected journals.")
          )
        ),
        fluidRow(
          box(width = 12, title = "Growth Leaders", status = "success", solidHeader = TRUE,
              uiOutput("trendInsights")
          )
        )
      ),

      # --- Geography Tab ---
      tabItem(tabName = "geography",
        fluidRow(
          column(width = 3,
            box(width = 12, title = "Filters", status = "primary", solidHeader = TRUE,
                checkboxGroupInput("geoYear", "Years:", choices = NULL, inline = TRUE), # Choices loaded server-side
                selectInput("geoJournals", "Journals:", choices = NULL, multiple = TRUE), # Choices loaded server-side
                hr(),
                p("Filter by specific journals to see their combined global footprint. To capture the real footprint of GIScience research, for each article, each *unique* jurisdiction (country) is counted with full score. For example, a 3-author article where 2 authors are based in US institutions, while the other one is in Germany counts as one publication for US and one publication for Germany.")
            )
          ),
          column(width = 9,
             box(width = 12, title = "Global Distribution", status = "primary", solidHeader = TRUE,
                 leafletOutput("mapPlot", height = "400px"),
                 helpText("Number of articles in selected journals in selected years.")
             ),
             box(width = 12, title = "Top 10 Producing Jurisdictions", status = "primary", solidHeader = TRUE,
                 plotlyOutput("countryBarPlot", height = "300px"),
                 helpText("Total number of articles involved in by country (selected journals and selected years)")
             )
          )
        )
      ),

      # --- Collaboration Tab ---
      tabItem(tabName = "collab",
        fluidRow(
          box(width = 12, title = "International Collaboration", status = "primary", solidHeader = TRUE,
              plotlyOutput("collabEvolPlot", height = "300px"),
              helpText("Percentage of articles involving international collaboration / cross-border articles (i.e. authors are from at least two different jurisdictions).")
          )
        ),
        fluidRow(
          box(width = 12, title = "Internatialization", status = "primary", solidHeader = TRUE,
              plotlyOutput("crossBorderPlot", height = "500px"),
              helpText("Distribution of the percentage of cross-border articles (i.e. authors are from different jurisdictions) per journal. The vertical line represents the field average. Journals on the right are more international than the average.")
          )
        ),
        fluidRow(
          box(width = 12, title = "Network Controls", status = "warning", solidHeader = TRUE,
              column(12, 
                  sliderInput("minCollab", "Minimum Collaborations:", min = 1, max = 20, value = 2, step = 1)
              ),
              helpText("To capture the real footprint of GIScience research and collaboration, each *unique* jurisdiction (country) is counted with full score. For example, a 3-author article where 2 authors are based in US institutions, while the other one is in Germany counts as one US - Germany pair.")
          )
        ),
        fluidRow(
          column(width = 6,
            box(width = 12, title = "International Collaboration Network", status = "warning", solidHeader = TRUE,
                visNetworkOutput("collabNet", height = "600px")
            )
          ),
          column(width = 6,
            box(width = 12, title = "Key Collaboration Pairs", status = "warning", solidHeader = TRUE,
                DTOutput("collabTable", height = "600px")
            )
          )
        )
      ),

      # --- Journal Insights Tab ---
      tabItem(tabName = "insights",
        fluidRow(
          box(width = 12, title = "Cost vs Impact: APC and Rankings", status = "warning", solidHeader = TRUE,
              column(12,
                 selectInput("apcRankMetric", "Select Ranking Metric:", 
                             choices = c("Impact Factor" = "Rank_IF", 
                                         "CiteScore" = "Rank_CiteScore", 
                                         "SJR" = "Rank_SJR", 
                                         "SNIP" = "Rank_SNIP",
                                         "5-Year IF" = "Rank_IF5y",
                                         "Immediacy Index" = "Rank_Imm",
                                         "Article Influence" = "Rank_Influ",
                                         "Eigenfactor" = "Rank_Eigen"),
                             selected = "Rank_SJR")
              ),
              plotlyOutput("apcRankPlot", height = "500px"),
              helpText("Explore the relationship between Article Processing Charges (APC) and Journal Rank. Generally, journals towards the origin (lower cost, better rankings) are more desirable.")

          )
        ),
        fluidRow(
          box(width = 12, title = "Open Access Adoption Rates", status = "success", solidHeader = TRUE,
              plotlyOutput("oaRealityPlot", height = "600px"),
              helpText("The vertical line represents the percentage of Open Access articles in the entire dataset. Journals to the right exceed this average.")
          )
        ),
        fluidRow(
          box(width = 12, title = "Publisher Market Share (Volume)", status = "info", solidHeader = TRUE,
              plotlyOutput("publisherPlot", height = "500px"),
              helpText("The share of each publisher in GIScience journal publications.")
          )
        )
      ),

      # --- About Tab ---
      tabItem(tabName = "about",
        fluidRow(
          box(width = 12, title = "About this Dashboard", status = "primary", solidHeader = TRUE,
              HTML(
                 "<p>This dashboard accompanies the paper <a href='https://doi.org/10.1080/13658816.2024.2347306' target='_blank'><b>Assessing publication trends in selected GIScience journals</b></a> published in the <i>International Journal of Geographical Information Science</i>. The goal is to help the GIScience community find suitable venues for publications and assess our field's evolution.</p>
                 <p><b>A Note on Metrics:</b> While journal metrics play an embedded role in academic culture, often influencing promotion, tenure, and funding decisions, it is crucial to recognize their limitations. <b>Journal metrics are widely regarded as insufficient proxies for the quality and real-world impact of individual research</b>, especially when used in the context of individual research or authors. They aggregate performance at the venue level rather than assessing the merit of specific articles. We encourage readers to engage with the principles of the <a href='https://sfdora.org/' target='_blank'>San Francisco Declaration on Research Assessment (DORA)</a>, which advocates for evaluating research on its own merits rather than the prestige of the journal where it is published.</p>

                 <p>Still, we hope that this tool is useful for GIScientists in all career stages. You can use it to:</p>
                 <ul>
                   <li><b>Track Evolution</b>: See how 24 major journals have changed since 2018.</li>
                   <li><b>Analyze Metrics</b>: Compare commonly used journal metrics.</li>
                   <li><b>Discover Networks</b>: Interactively navigate the web of international collaboration.</li>
                 </ul>
                 <p>Developed by the <a href='https://gatorlab.io' target='_blank'>GATOR Lab</a> (<b>G</b>eospatial <b>A</b>nalytics, <b>T</b>echnology and <b>O</b>pen <b>R</b>esearch) at the University of Florida. The initial Shiny dashboard was created by <a href='https://gatorlab.io/people/students/corcino.html' target='_blank'>Jorge Corcino</a>.</p>
                <p>Juhász, L. (2024) Assessing publication trends in selected GIScience journals. <i>Int. J. Geogr. Inf. Sci.</i> 38(8), 1443–1467. DOI: <a href='https://doi.org/10.1080/13658816.2024.2347306' target='_blank'>10.1080/13658816.2024.2347306</a></p>"
              )
          )
        ),
        fluidRow(
          box(width = 12, title = "Methodology", status = "info", solidHeader = TRUE,
              HTML(
                "<p>This dashboard considers citable items (articles, reviews) published in selected GIScience journals. The list of journals included here extends previous lists in the literature
                by adding a few emerging journals. It is important to note that no authoritative and widely accepted list of GIScience venues exists for several reasons, including the 
                difficulty of defining our field. Therefore, this list of journals (and any list attempting to do the same) is inherently subjective.</p>
                <p>Published articles and their metadata are sourced from <a href='https://dev.elsevier.com/sc_apis.html' target='_blank'>Scopus</a>. Journal metrics and other data are compiled in a manual-automatic way from various sources, including <a href='https://jcr.clarivate.com/jcr/home?app=jcr' target='_blank'>JCR</a>, <a href='https://www.scimagojr.com/' target='_blank'>SCImago</a> and <a href='https://www.journalindicators.com/indicators' target='_blank'>CWTS</a></br></br>
                For each article, each contributing jurisdiction is counted with the same weight to show the real footprint of GIScience research regardless of the number of authors and their order.</br></br>
                More details about the methodology, background  as well as initial processing scripts are given by Juhász (2024). Updated software as well as the source code of this dashboard will be made available here.</p>"
              )
          )
        ),
        fluidRow(
          box(width = 12, title = "Included journal evaluation metrics", status = "success", solidHeader = TRUE,
              HTML(
                "<p>The following journal metrics are considered in this dashboard:</p>
                <ul>
                  <li><b>Impact factor:</b> Measures the average number of citations received in a particular year by papers published in the journal during the two preceding years.</li>
                  <li><b>CiteScore:</b> Calculates the average citations received per document published in the journal. It considers a four-year publication window, offering a broader scope than the IF.</li>
                  <li><b>Source normalized impact per paper (SNIP):</b> Measures the contextual citation impact by weighting citations based on the total number of citations in a subject field. It provides a more field-specific understanding of citation impact.</li>
                  <li><b>SCImago Journal Rank (SJR):</b> Measures the scientific influence of the average article in a journal. It considers both the number of citations received by a journal and the importance of the journals where such citations come from.</li>
                  <li><b>5-Year impact factor (IF5yr):</b> Similar to the traditional IF, this metric averages the number of citations received in a particular year by papers published in the journal in the previous five years.</li>
                  <li><b>Immediacy Index (Imm):</b> Measures the average number of citations received in a given year by articles published in the same year. It helps gauge how quickly articles in a journal are cited upon publication.</li>
                  <li><b>Article Influence Score (Influ):</b> This metric reflects the average influence of a journal’s articles over the first five years after publication. It is calculated by dividing the Eigenfactor Score by the number of articles in the journal, normalized as a fraction of all articles in all publications.</li>
                  <li><b>Normalized Eigenfactor (Eigen):</b> The calculation is based on the number of times articles from the journal published in the past five years have been cited in the current year. This metric adjusts for differences in citation practices across disciplines, making it possible to compare journals from different fields.</li>
                </ul>
              ")
          )
        ),
        fluidRow(
          box(width = 12, title = "Limitations", status = "warning", solidHeader = TRUE,
              HTML(
                "<p>Here are a couple things to keep in mind.</p>
                <ul>
                  <li>Journal metrics are not representative of research quality</li>
                  <li>Not all GIScience research is distributed as journal articles.</li>
                  <li>GIScience research is often distributed outside of the journals tracked in this dashboard.</li>
                  <li>Authors may prefer to publish in languages other than English, which makes this dashboard skewed towards English-language publications and Western perspectives.</li>
                  <li>The dashboard considers special administrative regions (e.g. Hong Kong, Macau) and other non-sovereign entities, therefore, term jurisdiction is adapted instead of country to be more inclusive.</li> 
                </ul>
                "
              )
          )
        )
      )
    )
  )
)

server <- function(input, output, session) {

  # DEBUG: Check data availability
  print("SERVER STARTED")
  print(paste("Journal Meta Rows:", if(exists("journal_meta")) nrow(journal_meta) else "MISSING"))
  print(paste("Metrics Ranked Rows:", if(exists("metrics_ranked")) nrow(metrics_ranked) else "MISSING"))
  print(paste("Articles DF Rows:", if(exists("articles_df")) nrow(articles_df) else "MISSING"))
  print(paste("Countries DF Rows:", if(exists("countries_df")) nrow(countries_df) else "MISSING"))

  # --- Overview Stats ---
  # --- Overview Stats ---
  output$totalJournalsBox <- renderValueBox({
    valueBox(length(unique(journal_meta$ID)), "Journals Tracked", icon = icon("book"), color = "blue")
  })

  output$totalArticlesBox <- renderValueBox({
    total_arts <- if(nrow(articles_df) > 0) sum(articles_df$article, na.rm=TRUE) else 0
    valueBox(format(total_arts, big.mark=","), "Total Articles (2018 - 2024)", icon = icon("file-alt"), color = "green")
  })

  output$oaShareBox <- renderValueBox({
    total_arts <- if(nrow(articles_df) > 0) sum(articles_df$article, na.rm=TRUE) else 0
    total_oa <- if(nrow(articles_df) > 0) sum(articles_df$open_access, na.rm=TRUE) else 0

    pct <- if(total_arts > 0) round((total_oa / total_arts) * 100, 1) else 0
    valueBox(paste0(pct, "%"), "Open Access Share (2018 - 2024)", icon = icon("lock-open"), color = "yellow")
  })

  output$avgHBox <- renderValueBox({
    # Assuming 'H.index' column exists from sanitization of 'H-index'
    avg_h <- if(nrow(journal_meta) > 0) mean(as.numeric(journal_meta$H.index), na.rm=TRUE) else 0
    valueBox(round(avg_h, 1), "Average H-Index", icon = icon("chart-line"), color = "red")
  })

  output$journalMetaTable <- renderDT({
     req(journal_meta)

     # Create display dataframe
     df_display <- journal_meta %>%
       mutate(
         Journal = paste0("<a href='", website, "' target='_blank'>", Journal, "</a>")
       ) %>%
       select(ID, Journal, Publisher, `Peer Review` = Peer.review, OA, Preprint, `APC (US$)` = `APC..US..`, Notes)

     datatable(df_display, 
               escape = FALSE, # Allow HTML in cells
               options = list(pageLength = 10, scrollX = TRUE),
               rownames = FALSE)
  })

  # --- Meta Ranking Plot ---
  output$metaRankPlot <- renderPlotly({
    req(input$metaRankYear, input$metaRankMetrics, nrow(metrics_ranked) > 0)

    # Filter and Calculation
    selected_metrics <- input$metaRankMetrics

    # Ensure selected cols exist
    available_cols <- intersect(selected_metrics, names(metrics_ranked))
    if(length(available_cols) == 0) return(NULL)

    df_meta <- metrics_ranked %>%
      filter(year == as.numeric(input$metaRankYear)) %>%
      rowwise() %>%
      mutate(
        Mean_Rank = mean(c_across(all_of(available_cols)), na.rm = TRUE),
        Min_Rank = min(c_across(all_of(available_cols)), na.rm = TRUE),
        Max_Rank = max(c_across(all_of(available_cols)), na.rm = TRUE)
      ) %>%
      ungroup() %>%
      filter(!is.na(Mean_Rank)) %>%
      arrange(Mean_Rank) # Best rank (smallest number) first

    # Journal sorting for Y axis (Best at top)
    df_meta$ID <- factor(df_meta$ID, levels = rev(df_meta$ID)) # rev so 1 is at top

    # Background Shapes (approximate quartiles for 24 items: 1-6, 7-12, 13-18, 19-24)
    shapes <- list(
      list(type = "rect", x0 = 0, x1 = 6.5, y0 = -1, y1 = nrow(df_meta), fillcolor = "rgba(144, 238, 144, 0.3)", line = list(width = 0)), # Green
      list(type = "rect", x0 = 6.5, x1 = 12.5, y0 = -1, y1 = nrow(df_meta), fillcolor = "rgba(255, 255, 224, 0.3)", line = list(width = 0)), # Light Yellow
      list(type = "rect", x0 = 12.5, x1 = 18.5, y0 = -1, y1 = nrow(df_meta), fillcolor = "rgba(255, 218, 185, 0.3)", line = list(width = 0)), # Peach/Orange
      list(type = "rect", x0 = 18.5, x1 = 30, y0 = -1, y1 = nrow(df_meta), fillcolor = "rgba(255, 182, 193, 0.3)", line = list(width = 0))  # Red
    )

    plot_ly(df_meta, x = ~Mean_Rank, y = ~ID, type = 'scatter', mode = 'markers',
            marker = list(color = 'black', size = 10),
            error_x = list(
              type = 'data',
              symmetric = FALSE,
              array = ~Max_Rank - Mean_Rank,
              arrayminus = ~Mean_Rank - Min_Rank,
              color = 'black'
            ),
            hoverinfo = 'text',
            text = ~paste("<b>", ID, "</b><br>Mean Rank:", round(Mean_Rank, 1), 
                          "<br>Range:", Min_Rank, "-", Max_Rank)
    ) %>%
      layout(
        title = paste("Meta Ranking", input$metaRankYear),
        xaxis = list(title = "Rank (Lower is Better)", range = c(1, 24), gridcolor = 'lightgray'),
        yaxis = list(title = "", tickfont=list(size=11)),
        shapes = shapes,
        margin = list(l = 100) # Space for journal labels
      )
  })

  # --- Overview Plot ---
  output$overviewPlot <- renderPlotly({
    req(nrow(articles_df) > 0)

    df <- articles_df %>%
      group_by(year) %>%
      summarise(count = sum(article), .groups='drop')

    p <- ggplot(df, aes(x = year, y = count)) +
      geom_bar(stat = "identity", fill = "#3c8dbc") +
      theme_minimal() +
      labs(x = "Year", y = "Total Articles")

    ggplotly(p)
  })

  output$overviewTable <- renderTable({
    req(exists("journal_meta"))

    # Select columns if they exist (sanitized names)
    cols <- c("ID", "Journal", "H.index", "OA.model")
    cols <- intersect(cols, names(journal_meta))

    journal_meta %>%
      arrange(desc(H.index)) %>%
      slice(1:5) %>%
      select(all_of(cols))
  },  width = "100%", spacing = "xs")

  # --- Dynamic Insights Logic ---
  output$dynamicInsights <- renderUI({
    req(exists("articles_df"), exists("countries_df"), exists("metrics_ranked"))

    # 1. Top Country (Handling the aggregated format)
    top_country_data <- countries_df %>%
      group_by(country) %>%
      summarise(total = sum(num), .groups='drop') %>%
      arrange(desc(total)) %>%
      slice(1)

    top_country_str <- paste0("The leading contributor is <b>", top_country_data$country, "</b> with ", 
                              format(top_country_data$total, big.mark=","), " articles.")

    # 2. Top Journal by Impact Factor (Latest Year)
    latest_metric_year <- max(metrics_ranked$year[!is.na(metrics_ranked$IF)], na.rm=TRUE)
    top_journal_data <- metrics_ranked %>%
      filter(year == latest_metric_year) %>%
      arrange(desc(IF)) %>%
      slice(1)

    top_journal_str <- paste0("The most influential journal is <b>", top_journal_data$ID, 
                              "</b> with an Impact Factor of ", round(top_journal_data$IF, 2), 
                              " (", latest_metric_year, ").")

    # 3. Overall Volume Trend (First Year vs Last Year)
    # Filter out NA years (which caused the 'since NA' bug)
