    df_oa <- articles_df %>%
      group_by(journal) %>%
      summarise(
        pct_oa = sum(open_access) / sum(article) * 100,
        total = sum(article),
        .groups = 'drop'
      ) %>%
      mutate(
        diff = pct_oa - global_oa_pct,
        journal = reorder(journal, pct_oa) # Order by % OA
      )

    p <- ggplot(df_oa, aes(x = pct_oa, y = journal, color = journal)) +
      # Segments (Lollipop Sticks)
      geom_segment(aes(x = global_oa_pct, xend = pct_oa, y = journal, yend = journal), size = 1) +
      # Points (Lollipop Heads)
      geom_point(aes(text = paste(journal, "<br>% OA:", round(pct_oa, 1), "%<br>Articles:", total)), size = 4) +
      # Reference Line
      geom_vline(xintercept = global_oa_pct, color = "gray40", linetype = "dashed") +
      scale_color_manual(values = journal_colors) +
      theme_minimal() +
      labs(title = NULL, x = "Percentage of Open Access Articles", y = NULL) +
      theme(legend.position = "none")

    ggplotly(p, tooltip = "text")
  })

  output$publisherPlot <- renderPlotly({
    req(exists("journal_meta"), nrow(articles_df) > 0)

    # Aggregate Articles by Journal ID
    df_vol <- articles_df %>%
      group_by(journal) %>%
      summarise(volume = sum(article), .groups='drop')

    # Join with Metadata to get Publisher
    # journal_meta has ID and Publisher
    df_tree <- df_vol %>%
      left_join(journal_meta, by = c("journal" = "ID")) %>%
      filter(!is.na(Publisher)) %>%
      group_by(Publisher) %>%
      summarise(count = sum(volume), journals = paste(unique(journal), collapse=", "), .groups='drop')

    plot_ly(
      df_tree,
      labels = ~Publisher,
      parents = NA, # Top level
      values = ~count,
      type = 'treemap',
      textinfo = "label+value+percent parent",
      hovertemplate = "<b>%{label}</b><br>Articles: %{value}<br>Journals: %{customdata}<extra></extra>",
      customdata = ~journals
    ) %>% layout(title = "Article Volume by Publisher")
  })

}

